import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function ProfilePage() {
  const [avatar, setAvatar] = useState<string | null>(null);
  const [stats, setStats] = useState({
    rating: 0,
    matches: 0,
    wins: 0,
  });

  useEffect(() => {
    const userId = localStorage.getItem('userId');

    const fetchStats = async () => {
      try {
        const response = await axios.get('http://localhost:50053/stats', { params: { id: userId } }); 
        setStats(response.data);
      } catch (error) {
        console.error('Ошибка при получении данных', error);
      }
    };

    fetchStats();
  }, []);

  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatar(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3 text-center">
          <h1>Имя пользователя</h1>
          <div className="mt-4">
            <label htmlFor="avatar-upload" className="d-inline-block">
              <img
                src={avatar || 'https://via.placeholder.com/100'}
                alt="Avatar"
                className="rounded-circle"
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
            </label>
            <input
              id="avatar-upload"
              type="file"
              accept="image/*"
              onChange={handleAvatarChange}
              style={{ display: 'none' }}
            />
          </div>

          {/* Таблица статистики в сетке */}
          <div className="mt-4 border p-3">
            <div className="row border-bottom">
              <div className="col-6 text-right font-weight-bold">Рейтинг:</div>
              <div className="col-6 text-left">{stats.rating}</div>
            </div>
            <div className="row border-bottom">
              <div className="col-6 text-right font-weight-bold">Матчи:</div>
              <div className="col-6 text-left">{stats.matches}</div>
            </div>
            <div className="row border-bottom">
              <div className="col-6 text-right font-weight-bold">Победы:</div>
              <div className="col-6 text-left">{stats.wins}</div>
            </div>
            <div className="row">
              <div className="col-6 text-right font-weight-bold">Поражения:</div>
              <div className="col-6 text-left">{stats.matches - stats.wins}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
