// src/App.tsx
import React, { useState } from 'react';
import './App.css';
import RegistrationForm from './components/Registration';
import LoginForm from './components/Login';
import Navbar from './components/Navbar';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState<boolean>(false);

  const handleLogin = () => {
    setIsAuthenticated(true);  // Устанавливаем статус авторизованного пользователя
  };

  const handleLogout = () => {
    setIsAuthenticated(false);  // Очищаем статус при выходе
  };

  return (
    <div className="App">
      <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />

      <main>
        {!isAuthenticated && (
          <>
            <RegistrationForm onSuccess={handleLogin} />
            <LoginForm onSuccess={handleLogin} />
          </>
        )}
        {isAuthenticated && <h1>Добро пожаловать!</h1>}
      </main>
    </div>
  );
}

export default App;
