import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";

axios.defaults.withCredentials = true;

const Navbar = () => {
  // State to track login status
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("userId"));

  // Listen for changes to login status
  useEffect(() => {
    const handleStorageChange = () => {
      setIsLoggedIn(!!localStorage.getItem("userId"));
    };

    window.addEventListener("storage", handleStorageChange);
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  const logout = async (e: React.MouseEvent<HTMLAnchorElement>) => {
    e.preventDefault();
    if (localStorage.getItem("lobbyId")) {
      await axios.post(
        "http://localhost:50051/lobby/leave/" +
          localStorage.getItem("lobbyId") +
          "?user_id=" +
          localStorage.getItem("userId"),
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      localStorage.removeItem("lobbyId");
    }

    try {
      await axios.post(
        "http://localhost:50052/logout",
        {},
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      localStorage.removeItem("userId");
      setIsLoggedIn(false); // Update login state after logout
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          Monopoli
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            {/* Always visible link */}
            <li className="nav-item">
              <Link className="nav-link active" to="/home">
                Поиск игры
              </Link>
            </li>
            {/* Conditional rendering for logged-in state */}
            {isLoggedIn ? (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/profile">
                    Профиль
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to="/login" className="nav-link" onClick={logout}>
                    Logout
                  </Link>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/register">
                    Регистрация
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link active" to="/login">
                    Логин
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
