import React, { useState } from "react";
import axios from "axios";
import "../../style-page/style-lobby.css";

interface ModalWindowLobbyProps {
  closeLobby: () => void;
  fetchLobbies: () => void;
  name: string;
  password: string;
  pack: string;
  userSlots: number;
  handleLobbyNameChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  handlePasswordChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  handlePackChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  handleUserQuantityClick: (quantity: number) => void;
  handleCreateLobby: (e: React.FormEvent<HTMLFormElement>) => Promise<void>;
}

const ModalWindowLobby: React.FC<ModalWindowLobbyProps> = ({ 
  closeLobby, 
  fetchLobbies, 
  name, 
  password, 
  pack, 
  userSlots, 
  handleLobbyNameChange, 
  handlePasswordChange, 
  handlePackChange, 
  handleUserQuantityClick, 
  handleCreateLobby 
}) => {
  return (
    <div className="lobby-modal">
      <div className="lobby-content">
        <h2>Create lobby</h2>
        <form onSubmit={handleCreateLobby}>
          <input
            type="text"
            placeholder="Lobby name"
            value={name}
            onChange={handleLobbyNameChange}
            className="lobby-input"
          />
          <input
            type="text"
            placeholder="Password (optional)"
            value={password}
            onChange={handlePasswordChange}
            className="lobby-input"
          />
          <div className="player-quantity-buttons">
            {[2, 3, 4].map((num) => (
              <button
                key={num}
                type="button"
                className={`player-quantity-button ${userSlots === num ? "selected" : ""}`}
                onClick={() => handleUserQuantityClick(num)}
              >
                {num}
              </button>
            ))}
          </div>
          <input
            type="text"
            placeholder="Пакет"
            value={pack}
            onChange={handlePackChange}
            className="lobby-input"
          />
          <button type="submit" className="submit-button">
            Create
          </button>
          <button type="button" className="close-button" onClick={closeLobby}>
            Close
          </button>
        </form>
      </div>
    </div>
  );
};

export default ModalWindowLobby;
